-- ----------------------------
-- Table structure for `article`
-- ----------------------------
DROP TABLE IF EXISTS `article`;
CREATE TABLE `article` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `aid` int(11) unsigned NOT NULL DEFAULT 0,
  `article_category` tinyint(4) unsigned NOT NULL DEFAULT 0,
  `article_seokeywords` text NOT NULL DEFAULT '',
  `article_title` varchar(255) NOT NULL DEFAULT '',
  `article_thumb` varchar(255) NOT NULL DEFAULT '',
  `article_desctxt` text NOT NULL DEFAULT '',
  `article_bodyhtml` mediumtext NOT NULL DEFAULT '',
  `article_viewnum` int(11) unsigned NOT NULL DEFAULT 0,
  `article_createtime` int(11) unsigned NOT NULL DEFAULT 0,
  `article_isdelete` tinyint(4) unsigned NOT NULL DEFAULT 0,
  PRIMARY KEY (`id`),
  UNIQUE KEY `id` (`id`),
  KEY `article_countviewnum` (`article_viewnum`)
) ENGINE=InnoDB AUTO_INCREMENT=20000 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

